let active=!1;const scroller=document.querySelector(".slider__button"),slider=document.querySelector(".slider"),before=document.querySelector(".slider__image-wrapper--before"),after=document.querySelector(".slider__image-wrapper--after");function scrollIt(e){let t=Math.max(0,Math.min(e,slider.offsetWidth)),r=slider.offsetWidth-t;before.style.width=t+"px",after.style.width=r+"px",scroller.style.left=t+"px"}scroller.addEventListener("mousedown",(()=>{active=!0,scroller.classList.add("slider__scrolling")})),document.body.addEventListener("mouseup",(()=>{active=!1,scroller.classList.remove("slider__scrolling")})),document.body.addEventListener("mouseleave",(()=>{active=!1,scroller.classList.remove("slider__scrolling")})),document.body.addEventListener("mousemove",(function(e){if(!active)return;let t=e.pageX;t-=slider.getBoundingClientRect().left,scrollIt(t)})),scroller.addEventListener("touchstart",(()=>{active=!0,scroller.classList.add("slider__scrolling")})),document.body.addEventListener("touchend",(()=>{active=!1,scroller.classList.remove("slider__scrolling")})),document.body.addEventListener("touchcancel",(()=>{active=!1,scroller.classList.remove("slider__scrolling")})),slider.addEventListener("touchmove",(function(e){if(!active)return;e.preventDefault();let t=e.touches[0].pageX;t-=slider.getBoundingClientRect().left,scrollIt(t)}));